/*  Coolors sloppy dark theme   */

:root {
    --bg: #230007;
    --bg-sec: #5A0002;
    --prim-text: #D7CF07;
    --sec-text: #D98324;
    --accent: #A40606;
}

html,
#header {
    color: var(--prim-text) !important;
    background-color: var(--bg) !important;
}

html,
h1,
h2,
h3,
h4,
h5,
h6,
p {
    color: var(--prim-text) !important;
}

.st0 {
    fill: var(--bg);
}

.link--secondary {
    color: var(--sec-text);
}

.link--secondary.is-hover,
.link--secondary:hover {
    color: var(--sec-text);
}

html.no-touchevents #header-cad #header-cad_description {
    color: var(--sec-text);
}

html.no-touchevents #header-cad #header-cad_company {
    color: var(--sec-text);
}

#page-loader,
#sticky-cad,
.carbon-cad,
.card-cad,
.floating-cad {
    background: var(--bg);
}

.spinner {
    border-color: var(--accent) rgba(0, 0, 0, .1) rgba(0, 0, 0, .1);
}

#generator_spacebar-suggestion,
.link--black {
    color: var(--sec-text);
}

.sub-header {
    background-color: var(--bg);
}
